project/contracts/GovernanceExecutor.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

/*
GovernanceExecutor.sol

Responsibilities:
- Accept a relayer attestation that a proposal passed on the verifier chain (markProposalPassed).
- Execute the action committed earlier on GovernanceRootPublisher if:
  1) the relayer attested the proposal passed,
  2) the provided actionData matches the previously published actionDataHash,
  3) the proposal hasn't been executed before.

ActionData format:
abi.encode(address target, bytes data)

This allows executing arbitrary encoded calls on the `target` contract (for example, vault.setUnbondingPeriod(...)).
Use with caution — ensure actions are safe and the relayer is trusted (or replaced with stronger attestation).
*/

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

interface IGovernanceRootPublisher {
    // getProposalCore returns:
    // (address proposer, bytes32 actionDataHash, uint256 snapshotBlock, uint256 ER_snapshot, bytes32 powerRoot, string memory metadata, uint256 createdAt)
    function getProposalCore(uint256 proposalId)
        external
        view
        returns (
            address proposer,
            bytes32 actionDataHash,
            uint256 snapshotBlock,
            uint256 ER_snapshot,
            bytes32 powerRoot,
            string memory metadata,
            uint256 createdAt
        );
}

contract GovernanceExecutor is Ownable, ReentrancyGuard {
    IGovernanceRootPublisher public governancePublisher;

    // Authorized relayer address (off-chain service)
    address public relayer;

    // proposalId => attested (true if relayer observed ProposalPassed and called markProposalPassed)
    mapping(uint256 => bool) public attestedPassed;

    // proposalId => executed
    mapping(uint256 => bool) public executed;

    event RelayerUpdated(address indexed oldRelayer, address indexed newRelayer);
    event GovernancePublisherUpdated(address indexed oldPub, address indexed newPub);
    event ProposalAttested(uint256 indexed proposalId, bytes32 actionDataHash, address indexed attestedBy);
    event Executed(uint256 indexed proposalId, address indexed target, bytes result);

    constructor(address _governancePublisher, address _relayer) Ownable(msg.sender) {
        require(_governancePublisher != address(0), "publisher=0");
        governancePublisher = IGovernanceRootPublisher(_governancePublisher);
        relayer = _relayer;
    }

    /// @notice Owner can update the relayer address
    function setRelayer(address _relayer) external onlyOwner {
        emit RelayerUpdated(relayer, _relayer);
        relayer = _relayer;
    }

    /// @notice Owner can update the governance publisher address (emergency/admin)
    function setGovernancePublisher(address _publisher) external onlyOwner {
        require(_publisher != address(0), "publisher=0");
        emit GovernancePublisherUpdated(address(governancePublisher), _publisher);
        governancePublisher = IGovernanceRootPublisher(_publisher);
    }

    /// @notice Called by the relayer after observing ProposalPassed on the verifier chain.
    /// @param proposalId Proposal identifier.
    /// @param actionDataHash The actionDataHash observed in the ProposalPassed event (for safety/consistency).
    function markProposalPassed(uint256 proposalId, bytes32 actionDataHash) external {
        require(msg.sender == relayer, "only relayer");
        require(!attestedPassed[proposalId], "already attested");

        // Optionally, sanity-check: the stored actionDataHash in GovernanceRootPublisher should match provided hash.
        // We call governancePublisher.getProposalCore, which reverts if proposal not published.
        (, bytes32 storedHash, , , , , ) = governancePublisher.getProposalCore(proposalId);
        require(storedHash == actionDataHash, "actionDataHash mismatch with publisher");

        attestedPassed[proposalId] = true;
        emit ProposalAttested(proposalId, actionDataHash, msg.sender);
    }

    /// @notice Execute the action if the proposal was attested as passed and actionData matches the committed hash.
    /// @param proposalId Proposal identifier
    /// @param actionData abi.encode(address target, bytes data)
    function executeIfAuthorized(uint256 proposalId, bytes calldata actionData) external nonReentrant returns (bytes memory) {
        require(attestedPassed[proposalId], "proposal not attested as passed");
        require(!executed[proposalId], "already executed");

        // Fetch committed action data hash from publisher
        (, bytes32 storedHash, , , , , ) = governancePublisher.getProposalCore(proposalId);
        bytes32 providedHash = keccak256(actionData);
        require(storedHash == providedHash, "actionData hash mismatch");

        // Decode actionData: (address target, bytes data)
        (address target, bytes memory data) = abi.decode(actionData, (address, bytes));

        require(target != address(0), "invalid target");

        // Execute low-level call
        (bool ok, bytes memory result) = target.call(data);
        require(ok, "execution failed");

        executed[proposalId] = true;

        emit Executed(proposalId, target, result);
        return result;
    }

    /// @notice Emergency: owner can mark executed or attested flags (only for admin/testing).
    function adminSetAttested(uint256 proposalId, bool val) external onlyOwner {
        attestedPassed[proposalId] = val;
    }

    function adminSetExecuted(uint256 proposalId, bool val) external onlyOwner {
        executed[proposalId] = val;
    }
}

project/contracts/GovernanceRootPublishe.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

/// @title GovernanceRootPublisher
/// @notice Publishes governance proposals on Chain A by storing:
///  - actionDataHash (keccak256 of the actionData to be executed later)
///  - snapshotBlock (block number used to sample balances)
///  - ER_snapshot  (exchange rate at snapshot, WAD scaled = 1e18)
///  - powerRoot    (Merkle root containing per-account voting power for snapshot)
///  -  metadata ()


contract GovernanceRootPublisher{

    enum ProposalState { Unknown, Published }

    struct Proposal {
        address proposer;      // who published
        bytes32 actionDataHash; // keccak256(actionData)
        uint256 snapshotBlock; // block number for snapshot
        uint256 ER_snapshot;   // exchange rate at snapshot (WAD-scaled)
        bytes32 powerRoot;     // merkle root of (account, power, nonce) leaves
        string metadata;       // optional IPFS/metadata URI
        uint256 createdAt;     // timestamp of publish
        ProposalState state;
    }

    /// @notice proposalId => Proposal
    mapping(uint256 => Proposal) public proposals;

    /// @notice Emitted when a proposal is published and frozen on-chain.
    event ProposalCreated(
        uint256 indexed proposalId,
        address indexed proposer,
        bytes32 indexed actionDataHash,
        uint256 snapshotBlock,
        uint256 ER_snapshot,
        bytes32 powerRoot,
        string metadata,
        uint256 createdAt
    );

    /// @notice Emitted when proposer updates metadata (non critical)
    event ProposalMetadataUpdated(uint256 indexed proposalId, string metadata);

    constructor() {}

    /// @notice Publish (freeze) a proposal.
    /// @param proposalId Unique ID for the proposal (must not be used before).
    /// @param actionDataHash keccak256(actionData) - commitment to the execution payload
    /// @param snapshotBlock Block number at which balances were sampled for the Merkle leaves
    /// @param ER_snapshot Exchange rate at snapshot (WAD scaled, 1e18). Caller should read from vault at snapshot (off-chain).
    /// @param powerRoot Merkle root of (account, uint256 power, uint256 nonce) leaves representing voting power.
    /// @param metadata Optional metadata URI (IPFS link or JSON).
    function publishProposal(
        uint256 proposalId,
        bytes32 actionDataHash,
        uint256 snapshotBlock,
        uint256 ER_snapshot,
        bytes32 powerRoot,
        string calldata metadata
    ) external {
        require(proposalId != 0, "proposalId=0");
        require(actionDataHash != bytes32(0), "actionDataHash=0");
        require(powerRoot != bytes32(0), "powerRoot=0");
        // snapshotBlock should reference a real block (allow equal to current block, or past block)
        require(snapshotBlock <= block.number, "snapshotBlock must be <= current block");

        Proposal storage p = proposals[proposalId];
        require(p.state == ProposalState.Unknown, "proposal exists");

        p.proposer = msg.sender;
        p.actionDataHash = actionDataHash;
        p.snapshotBlock = snapshotBlock;
        p.ER_snapshot = ER_snapshot; // expected to be WAD scaled (1e18)
        p.powerRoot = powerRoot;
        p.metadata = metadata;
        p.createdAt = block.timestamp;
        p.state = ProposalState.Published;

        emit ProposalCreated(
            proposalId,
            msg.sender,
            actionDataHash,
            snapshotBlock,
            ER_snapshot,
            powerRoot,
            metadata,
            block.timestamp
        );
    }

    /// @notice Returns whether a proposal is published.
    function isPublished(uint256 proposalId) external view returns (bool) {
        return proposals[proposalId].state == ProposalState.Published;
    }

    /// @notice Getter for core proposal fields.
    function getProposalCore(uint256 proposalId)
        external
        view
        returns (
            address proposer,
            bytes32 actionDataHash,
            uint256 snapshotBlock,
            uint256 ER_snapshot,
            bytes32 powerRoot,
            string memory metadata,
            uint256 createdAt
        )
    {
        Proposal storage p = proposals[proposalId];
        require(p.state == ProposalState.Published, "proposal not published");
        return (
            p.proposer,
            p.actionDataHash,
            p.snapshotBlock,
            p.ER_snapshot,
            p.powerRoot,
            p.metadata,
            p.createdAt
        );
    }

    /// @notice Allow the proposer to update non-critical metadata (e.g., point to IPFS JSON).
    /// Does not allow changing snapshot/ER/action hash/power root.
    function updateMetadata(uint256 proposalId, string calldata metadata) external {
        Proposal storage p = proposals[proposalId];
        require(p.state == ProposalState.Published, "proposal not published");
        require(p.proposer == msg.sender, "only proposer");
        p.metadata = metadata;
        emit ProposalMetadataUpdated(proposalId, metadata);
    }

}

project/contracts/LiquidStakingVault.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

/*
LiquidStakingVault.sol

Features:
- ERC20 shares token (no rebasing). Shares represent claim on underlying assets.
- exchangeRate = (totalAssetsAvailable * WAD) / totalShares  (WAD = 1e18)
- deposit: user transfers underlying asset -> mints shares
- mint: user supplies shares -> receives assets (optional; not implemented separately to avoid confusion)
- distributeRewards: pushes assets into vault (increases exchange rate)
- initiateWithdraw: burns shares, computes assetsOwed (floor), mints Withdrawal NFT with availableAt = now + unbondingPeriod
- claim: after availableAt, owner claims assetsOwed, NFT burned
- lockedAssets tracks assets reserved for pending withdrawals (excluded from exchangeRate)
*/

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";

contract LiquidStakingVault is ERC20, ReentrancyGuard, Ownable {
    using SafeERC20 for IERC20;

    // Underlying asset (staked token)
    IERC20 public immutable asset;

    // WAD fixed point scaling for exchange rate calculations
    uint256 public constant WAD = 1e18;

    // Assets that have been reserved for pending withdrawals (not part of exchange rate)
    uint256 public lockedAssets;

    // Unbonding period (seconds) for withdrawals
    uint256 public unbondingPeriod;

    // Withdrawal NFT contract
    WithdrawalNFT public withdrawalNFT;

    address public governanceExecutor;

    // Events
    event Deposited(address indexed user, uint256 assets, uint256 shares);
    event DistributedRewards(address indexed by, uint256 assets);
    event InitiatedWithdraw(address indexed user, uint256 sharesBurned, uint256 assetsOwed, uint256 withdrawalId, uint256 availableAt);
    event Claimed(address indexed user, uint256 withdrawalId, uint256 assetsPaid);
    event UnbondingPeriodUpdated(uint256 oldPeriod, uint256 newPeriod);

    constructor(
        address _asset,
        string memory _sharesName,
        string memory _sharesSymbol,
        uint256 _unbondingPeriod
    ) ERC20(_sharesName, _sharesSymbol) Ownable(msg.sender){
        require(_asset != address(0), "asset=0");
        asset = IERC20(_asset);
        unbondingPeriod = _unbondingPeriod;
        withdrawalNFT = new WithdrawalNFT("LST-Withdraw", "LSTW");
    }

    modifier onlyGovernanceExecutor() {
        require(msg.sender == governanceExecutor, "only governance");
        _;
    }

    // ---------------------
    // Public / External API
    // ---------------------

    /// @notice Deposit underlying assets and receive shares.
    /// @param assets Amount of underlying token to deposit.
    /// @return shares Minted shares.
    function deposit(uint256 assets) external nonReentrant returns (uint256 shares) {
        require(assets > 0, "zero assets");
        // Transfer assets in
        asset.safeTransferFrom(msg.sender, address(this), assets);

        uint256 _sharesTotal = totalSupply();
        uint256 sharesToMint;
        if (_sharesTotal == 0) {
            // initial deposit: 1:1 => shares = assets (shares decimals same as ERC20 decimals)
            // We will treat both token decimals as consistent in tests (commonly 18).
            sharesToMint = assets;
        } else {
            // shares = floor(assets * WAD / exchangeRate)
            uint256 er = exchangeRate(); // WAD scaled
            // shares = floor( assets * WAD / er )
            sharesToMint = Math.mulDiv(assets, WAD, er);
            require(sharesToMint > 0, "insufficient assets to mint shares");
        }

        _mint(msg.sender, sharesToMint);
        emit Deposited(msg.sender, assets, sharesToMint);
        return sharesToMint;
    }

    /// @notice Push rewards into the vault. Caller must approve asset transfer beforehand.
    /// This increases the assets available to share holders and therefore increases exchangeRate.
    function distributeRewards(uint256 assets) external nonReentrant {
        require(assets > 0, "zero assets");
        asset.safeTransferFrom(msg.sender, address(this), assets);
        emit DistributedRewards(msg.sender, assets);
    }

    /// @notice Initiate a withdrawal by burning shares and receiving a Withdrawal NFT.
    /// The NFT contains `assetsOwed` and `availableAt = now + unbondingPeriod`.
    /// @param shares Amount of shares to redeem.
    /// @return withdrawalId NFT id minted for the withdrawal.
    function initiateWithdraw(uint256 shares) external nonReentrant returns (uint256) {
        require(shares > 0, "zero shares");
        uint256 senderBalance = balanceOf(msg.sender);
        require(senderBalance >= shares, "insufficient shares");

        // Compute assetsOwed at current exchange rate (floor)
        uint256 assetsOwed = sharesToAssets(shares);

        // Burn shares
        _burn(msg.sender, shares);

        // Reserve the assets -- they belong to the withdrawal and shouldn't be counted in ER
        lockedAssets += assetsOwed;
        require(lockedAssets <= asset.balanceOf(address(this)), "insufficient asset balance after lock"); 
        // Mint withdrawal NFT to caller recording assetsOwed and availableAt
        uint256 availableAt = block.timestamp + unbondingPeriod;
        uint256 withdrawalId = withdrawalNFT.mintWithdrawal(msg.sender, assetsOwed, availableAt);

        emit InitiatedWithdraw(msg.sender, shares, assetsOwed, withdrawalId, availableAt);
        return withdrawalId;
    }

    /// @notice Claim a matured withdrawal after its unbonding period.
    /// Burns the Withdrawal NFT and transfers assets to the owner.
    /// @param withdrawalId NFT id.
    function claim(uint256 withdrawalId) external nonReentrant {
        require(withdrawalNFT.ownerOf(withdrawalId) == msg.sender, "not owner");
        (uint256 assetsOwed, uint256 availableAt) = withdrawalNFT.getWithdrawal(withdrawalId);
        require(block.timestamp >= availableAt, "not yet available");
        require(assetsOwed > 0, "nothing owed");

        // Mark as paid by burning NFT and updating lockedAssets
        withdrawalNFT.burnWithdrawal(withdrawalId);

        // Decrease locked assets and transfer
        require(lockedAssets >= assetsOwed, "lockedAssets underflow");
        lockedAssets -= assetsOwed;

        asset.safeTransfer(msg.sender, assetsOwed);
        emit Claimed(msg.sender, withdrawalId, assetsOwed);
    }

    // ---------------------
    // Views & Conversions
    // ---------------------

    /// @notice Returns the exchange rate as WAD (1e18). ER = totalAssetsAvailable * WAD / totalShares.
    /// totalAssetsAvailable = asset.balanceOf(this) - lockedAssets
    function exchangeRate() public view returns (uint256) {
        uint256 totalShares_ = totalSupply();
        if (totalShares_ == 0) {
            return WAD; // 1:1 if no shares exist
        }
        uint256 totalAssetsAvailable = asset.balanceOf(address(this));
        if (totalAssetsAvailable <= lockedAssets) {
            // If all assets are locked (rare), ER should be 0? To be safe we'll return 0 to avoid division by zero.
            return 0;
        }
        uint256 available = totalAssetsAvailable - lockedAssets;
        // ER = available * WAD / totalShares
        return Math.mulDiv(available, WAD, totalShares_);
    }

    /// @notice Convert shares to assets using current exchange rate (floor).
    function sharesToAssets(uint256 shares) public view returns (uint256) {
        uint256 er = exchangeRate(); // WAD
        if (er == 0) return 0;
        return Math.mulDiv(shares, er, WAD);
    }

    /// @notice Convert assets to shares using current exchange rate (floor).
    function assetsToShares(uint256 assets) public view returns (uint256) {
        uint256 totalShares_ = totalSupply();
        if (totalShares_ == 0) {
            // initial deposit: 1:1
            return assets;
        }
        uint256 er = exchangeRate(); // WAD
        if (er == 0) return 0;
        return Math.mulDiv(assets, WAD, er);
    }

    // ---------------------
    // Governance helpers
    // ---------------------

    function setGovernanceExecutor(address _governanceExecutor) external onlyOwner {
        require(_governanceExecutor != address(0), "governance=0");
        governanceExecutor = _governanceExecutor;
    }

    /// @notice Only owner can update unbonding period (can be later updated via GovernanceExecutor)
    function setUnbondingPeriod(uint256 newPeriod) external onlyGovernanceExecutor() {
        emit UnbondingPeriodUpdated(unbondingPeriod, newPeriod);
        unbondingPeriod = newPeriod;
    }

}

/*
WithdrawalNFT contract - minimal ERC721 to record withdrawals.

It provides:
- mintWithdrawal(owner, assetsOwed, availableAt) -> returns id
- getWithdrawal(id) -> (assetsOwed, availableAt)
- burnWithdrawal(id)
- ownerOf from ERC721

Note: NFT ownership controls claim permission.
*/
contract WithdrawalNFT is ERC721, Ownable {
    uint256 tokenId = 0;

    struct Withdrawal {
        uint256 assetsOwed;
        uint256 availableAt;
    }

    mapping(uint256 => Withdrawal) private _withdrawals;

    constructor(string memory name_, string memory symbol_) ERC721(name_, symbol_) Ownable(msg.sender) {}

    /// @notice Only vault (owner) can mint a withdrawal NFT to `to`.
    function mintWithdrawal(address to, uint256 assetsOwed, uint256 availableAt) external onlyOwner returns (uint256) {
        tokenId += 1;
        uint256 id = tokenId;
        _withdrawals[id] = Withdrawal({assetsOwed: assetsOwed, availableAt: availableAt});
        _safeMint(to, id);
        return id;
    }

    /// @notice Returns (assetsOwed, availableAt) for withdrawal id.
    function getWithdrawal(uint256 id) external view returns (uint256 assetsOwed, uint256 availableAt) {
        Withdrawal memory w = _withdrawals[id];
        return (w.assetsOwed, w.availableAt);
    }

    /// @notice Burn withdrawal. Only owner (vault) may burn entries on successful claim.
    function burnWithdrawal(uint256 id) external onlyOwner {
        // Clear storage and burn NFT
        delete _withdrawals[id];
        _burn(id);
    }

    // The vault (LiquidStakingVault) should be set as owner of this contract upon deployment.
    // In our design, LiquidStakingVault deploys WithdrawalNFT in its constructor and is therefore owner.
}

